import React, { useState, useEffect } from "react";
import styles from './style.less';
import { UploadOutlined, SnippetsOutlined } from '@ant-design/icons';
import { Button, Input, DatePicker, Upload, Form, message, Card, Image,Modal } from 'antd';
import { PageContainer } from '@ant-design/pro-layout';
import moment from 'moment';
import { connect } from 'umi';
import { updateDescribe } from './service'
const { TextArea } = Input;

//bug
const AvatarView = ({ avatar }) => {
  //图片上传到服务器
  const handleUploadChange = async (info) => {
    let url;
    if (info.file.response) {
      url = info.file.response.data.url
    }
    const result = await updateDescribe({
      busAvatar: url
    })
    if (result) {
      // message.success("修改成功");
      location.reload();
    }
  }
  const Uploadprops = {
    action: 'http://119.29.92.83:8003/ossservice/v1/auth/file/upload',
    onChange: handleUploadChange,
  }
  return (
    <div>
      <div className={styles.avatar_title}>
        头像
      </div>
      <div className={styles.avatar}>
        <Image
          src={avatar}
          alt="avatar"
          fallback="data:image/png;base64,"
          placeholder={
            <Image src={`${avatar}?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200`} />
          }
        />
      </div>
      <Upload {...Uploadprops} showUploadList={false} listType="picture">
        <div className={styles.button_view}>
          <Button><UploadOutlined />更换头像</Button>
        </div>
      </Upload>
    </div>
  );
}



const OrgInfo = props => {
  const {
    dispatch,
    loading,
    orgUserInfo: { info },
  } = props;
  let busBuildTime
  let avatar;
  let personPic;
  let license;
  if (info.describe) {
    avatar = info.describe.busAvatar
    personPic = info.describe.legalPersonPic
    license = info.describe.busLicense
    busBuildTime = info.describe.busBuildTime
  }
  const [form] = Form.useForm();
  console.log(info);
  //获取数据
  useEffect(() => {
    dispatch({
      type: 'orgUserInfo/fetch',
      payload: {
      },
    });
  }, [1]);
  //异步处理将数据放入表单
  useEffect(() => {
    form.setFieldsValue({
      ...info.describe,
      phone: info.phone,
      busBuildTime:moment(busBuildTime)
    })
  })
 
  const formLayout = {
    labelCol: {
      span: 7,
    },
    wrapperCol: {
      span: 13,
    },
  };

  const onFinish = async (params) => {
    console.log(params);
    let time = moment(params.busBuildTime).format('YYYY-MM-DD');
    console.log(time);
    const result = await updateDescribe({
      ...params,
      busBuildTime:time
    });
    if (result) {
      message.success("修改成功")
      location.reload()
    }
  }
  //查看身份证Modal
  function GetPersonPic(personPic) {
    Modal.info({
      title: '身份证',
      // width: "1100px",
      content: (
        <Image
          src={personPic}
          alt="avatar"
          fallback="data:image/png;base64,"
          placeholder={
            <Image src={`${personPic}?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200`} />
          }
        />
      )
    })
  }
  //查看营业执照Modal
  function GetLicense(license) {
    Modal.info({
      title: '营业执照',
      // width: "1100px",
      content: (
        <Image
        src={license}
        alt="avatar"
        fallback="data:image/png;base64,"
        placeholder={
          <Image src={`${license}?x-oss-process=image/blur,r_50,s_50/quality,q_1/resize,m_mfit,h_200,w_200`} />
        }
      />
      )
    })
  }


  return (
    <PageContainer title="我的信息">
      <Card
        loading={loading}
      >
        <div className={styles.baseView}>
          <div className={styles.left}>
            <Form
              // layout="vertical"
              {...formLayout}
              onFinish={onFinish}
              hideRequiredMark
              form={form}
            >
              <Form.Item
                name="legalPerson"
                label="法人姓名:"
              >
                <Input disabled />
              </Form.Item>
              <Button type="primary" onClick={() => { GetPersonPic(personPic) }} style={{ marginLeft: 140, marginBottom: 23 }}>
                <SnippetsOutlined />身份证
              </Button>
              <Button type="primary" onClick={() => { GetLicense(license) }} style={{ marginLeft: 30 }}>
                <SnippetsOutlined />营业执照
              </Button>
              <Form.Item
                name="busName"
                label="机构:"
              >
                <Input disabled />
              </Form.Item>
              <Form.Item
                name="phone"
                label="手机电话号码"
                rules={[
                  {
                    required: true,
                    message: "请输入正确手机号"
                  },
                ]}
              >
                <Input disabled/>
              </Form.Item>
              <Form.Item
                label="创办时间"
                name="busBuildTime"
              >
                <DatePicker />
              </Form.Item>
              <Form.Item
                name="busDescribe"
                label="机构描述"
                rules={[
                  {
                    required: true,
                    message: '请输入机构描述',
                  },
                ]}
              >
                <TextArea showCount maxLength={100} />
              </Form.Item>
              <Form.Item>
                <Button htmlType="submit" type="primary" style={{ marginLeft: "50%" }}>更新信息</Button>
              </Form.Item>
            </Form>
          </div>
          <div className={styles.right}>
            <AvatarView avatar={avatar} />
          </div>
        </div>
      </Card>
    </PageContainer>
  )
}
const mapStateToProps = ({ orgUserInfo, loading }) => {
  return {
    orgUserInfo,
    loading: loading.models.HomeList,
  }
}

export default connect(mapStateToProps)(OrgInfo);